const CommentsList = ({ comments }) => {
    // Create a map to track unique users
    const uniqueUsers = new Map();

    // Filter out duplicate comments based on postedBy
    const filteredComments = comments.filter(comment => {
        const user = typeof comment.postedBy === 'object' ? comment.postedBy.postedBy : comment.postedBy;
        if (!uniqueUsers.has(user)) {
            uniqueUsers.set(user, true);
            return true;
        }
        return false;
    });

    return (
        <>
            <h3>Comments:</h3>
            {filteredComments.map(comment => (
                <div className="comment" key={comment.postedBy + ': ' + comment.text}>
                    <h4>{typeof comment.postedBy === 'object' ? comment.postedBy.postedBy : comment.postedBy}</h4>
                    <p>{comment.text}</p>
                </div>
            ))}
        </>
    );
};

export default CommentsList;
